/**
 * Coachify Layout Settings
 *
 * @package Coachify
 */
if ( ! function_exists( 'coachify_customize_register_layout' ) ) : 

function coachify_customize_register_layout( $wp_customize ) {
	
    $defaults = coachify_get_general_defaults();

    /** Layout Settings */
    $wp_customize->add_panel(
        'layout_settings',
        array(
            'title'       => __( 'Layouts', 'coachify' ),
            'priority'    => 12,
            'capability'  => 'edit_theme_options',
            'description' => __( 'Change different Blog page & General Sidebar layout from here.', 'coachify' ),
        )
    );
    
    /** General Sidebar Layout */
    $wp_customize->add_section(
        'general_layout',
        array(
            'title'    => __( 'Sidebar Layouts', 'coachify' ),
            'panel'    => 'layout_settings',
        )
    );
    
    /** Page Sidebar layout */
    $wp_customize->add_setting( 
        'page_sidebar_layout', 
        array(
            'default'           => $defaults['page_sidebar_layout'],
            'sanitize_callback' => 'coachify_sanitize_radio'
        ) 
    );
    
    $wp_customize->add_control(
		new Coachify_Radio_Image_Control(
			$wp_customize,
			'page_sidebar_layout',
			array(
				'section'	  => 'general_layout',
				'label'		  => __( 'Page Sidebar Layout', 'coachify' ),
