    /**
     * List All Social Icons get theme mod function
     *
     * @return void
     */
    public function coachify_all_social_icons(){
        $social_array = [ 
            [
                'key' => 'chfy_facebook',
                'value' => '#'
            ],
            [
                'key' => 'chfy_twitter',
                'value' => '#'
            ],
            [
                'key' => 'chfy_instagram',
                'value' => '#'
            ],
            [
                'key' => 'chfy_pinterest',
                'value' => ''
            ],
            [
                'key' => 'chfy_youtube',
                'value' => ''
            ],
            [
                'key' => 'chfy_tiktok',
                'value' => ''
            ],
            [
                'key' => 'chfy_linkedin',
                'value' => ''
            ],
            [
                'key' => 'chfy_whatsapp',
                'value' => ''
            ],
            [
                'key' => 'chfy_viber',
                'value' => ''
            ],
            [
                'key' => 'chfy_telegram',
                'value' => ''
            ]
        ];
        $social_icons = apply_filters( 'chfy_social_icons',$social_array );
        if( ! $social_icons ) return;

        $list_social = [];
        foreach( $social_icons as $icon  ){
            $list_social[$icon['key']] = get_theme_mod( $icon['key'], $icon['value'] );
        }

        return $list_social;
    }

    /**
     * Social Links function
     *
     * @return void
     */
    public function coachify_social_links( $ed_social_media = false ){
        $defaults               = coachify_get_general_defaults();
        $ed_social_media_newtab = get_theme_mod( 'ed_social_links_new_tab', $defaults['ed_social_links_new_tab'] );
        $list_social            = $this->coachify_all_social_icons();
        $social_media_order     = get_theme_mod( 'social_media_order', $defaults['social_media_order']  );
        if( $ed_social_media && $social_media_order ){ ?>
                <ul class="social-networks">
                    <?php 
                    foreach( $social_media_order as $link ){
                        $actualsocialkey = str_replace( 'chfy_','',$link );
                        foreach( $list_social as $key => $value ){
                            if( !empty( $value ) && $key == $link ){
                                ?>
                                    <li>
                                    <a href="<?php if( isset( $value ) ) echo esc_url( $value ); ?>" target="<?php echo $ed_social_media_newtab ? '_blank': '_self'; ?>" rel="nofollow noopener" aria-label="<?php echo esc_attr($actualsocialkey); ?>">
                                        <?php echo wp_kses( $this->coachify_lists_all_svgs( $actualsocialkey ), coachify_get_kses_extended_ruleset() ); ?>
                                    </a>
                                </li>  
                                <?php 
                            }
                        }
                    } 
                    ?>
                </ul>
            <?php                            
        }
    }
